  content_preview INT(1) NOT NULL DEFAULT 0,
  credits INT(1) NOT NULL DEFAULT 0,
  blacklist INT(1) NOT NULL DEFAULT 0,

  orders_edit INT(1) NOT NULL DEFAULT 0,
