  PRIMARY KEY (orders_products_id),
  KEY idx_orders_id (orders_id),
  KEY idx_products_id (products_id),
  KEY idx_products_quantity(products_quantity)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS orders_status;
